import React from 'react';
import { useSelector } from 'react-redux';
import { Row, Col, PageHeader } from 'antd';
import AppLayout from '../components/Applayout';
import PostForm from '../components/postForm';
import PostCard from '../components/postCard';

const FreeBoard = () => {
  const { postInfos } = useSelector((state) => state.post);
  return (
    <>
      <AppLayout>
        <Row>
          <Col span={20}>
            <div style={{ marginLeft: '20px', marginTop: '10px', marginRight: '20px' }}>
              <PageHeader className="site-page-header" title="자유게시판" subTitle="하냥이들의 소통공간" />
              <PostForm />
              {postInfos.map((post) => <PostCard key={post.id} post={post} />)}
            </div>
          </Col>
          <Col span={4} style={{ backgroundColor: '#50bcdf' }}>
            광고였으면 좋겠다
          </Col>
        </Row>
      </AppLayout>
    </>
  );
};

export default FreeBoard;
