import React, { useEffect } from 'react';
import { useDispatch } from 'react-redux';
import { Card, Avatar } from 'antd';
import { SettingOutlined, EditOutlined, EllipsisOutlined } from '@ant-design/icons';
import propTypes from 'prop-types';
import { LOAD_POSTS_REQUEST } from '../reducers/post';


const PostCard = ({ post }) => {
  return (
    <>
      <Card
        style={{ margin: '10px 70px' }}
        hoverable
        cover={(
          <div>
            <Avatar src={`http://localhost:3065%{}`} style={{ float: 'left' }} />
            <div style={{ display: 'flex', flexDirection: 'column', marginLeft: '20px' }}>
              <strong style={{ flex: 1, marginLeft: '10px' }}>게시자 이름</strong>
              <p style={{ flex: 1, marginLeft: '10px' }}>7월2일 오후 10시:16</p>
            </div>

            <p>안녕하세요!안녕하세요!안녕하세요!안녕하세요!안녕하세요!안녕하세요!안녕하세요!안녕하세요!안녕하세요!안녕하세요!안녕하세요!</p>
            <img alt="example" src="https://gw.alipayobjects.com/zos/rmsportal/JiqGstEfoWAOHiTxclqi.png" />
          </div>
        )}
        actions={[
          <SettingOutlined key="setting" />,
          <EditOutlined key="edit" />,
          <EllipsisOutlined key="ellipsis" />,
        ]}
      />
    </>
  );
};

PostCard.propTypes = {
  post: propTypes.array.isRequired,
};

export default PostCard;
