import React from 'react';
import { useForm } from 'react-hook-form'

const Join = () => {
  const { register, handleSubmit, errors } = useForm();
  const onSubmit = (data) => {
    console.log(data);
  };

  return (
    <div style={{ backgroundColor: '#272727', height: '800px' }}>
      <form onSubmit={handleSubmit(onSubmit)}>
        <label htmlFor="id">ID</label>
        <input name="id" ref={register({ required: true, maxLength: 20 })} />
        <br />
        {errors.id && <span style={{ color: 'red' }}>id is required</span>}
        <br />
        <label htmlFor="email">Email</label>
        <input name="email" ref={register({ required: true, pattern: /^[A-Za-z]+$/i })} />
        <br />
        {errors.email && <span>email is required</span>}
        <br />
        <label htmlFor="nickname">Nickname</label>
        <input name="nickname" ref={register({ required: true })} />
        <br />
        {errors.nickname && <span>nickname is required</span>}
        <br />
        <label htmlFor="password">Password</label>
        <input name="password" type="password" ref={register({ required: true })} />
        <br />
        <label htmlFor="passwordCheck">재입력</label>
        <input name="passwordCheck" type="password" ref={register({ required: true })} />
        <br />
        {errors.nickname && <span>nickname is required</span>}
        <br />
        <select name="department" ref={register}>
          <option value="software">소프트웨어대학</option>
          <option value="engineering">공과대학</option>
          <option value="media">언론정보대학</option>
          <option value="economic">경상대학</option>
          <option value="science">과학기술융합대학</option>
          <option value="design">디자인대학</option>
          <option value="culture">국제문화대학</option>
          <option value="sport">예체능대학</option>
          <option value="pharmacy">약학대학</option>
        </select>
        <input type="submit" />
      </form>
    </div>
  );
};

export default Join;
