import React from 'react';
import { useForm } from 'react-hook-form';
import { useDispatch } from 'react-redux';
import { Input, Button, Label } from 'antd';
import { EyeInvisibleOutlined, EyeTwoTone } from '@ant-design/icons';
import { loginRequestAction } from '../reducers/user';

const LoginForm = () => {
  const { register, handleSubmit, watch, errors } = useForm();
  // console.log(watch('example')); // watch input value by passing the name of it

  const dispatch = useDispatch();

  const onSubmit = (data) => {
    dispatch(loginRequestAction({
      id: data.id,
      password: data.password,
    }));
    console.log(data.id, data.password);
  };

  return (
    <>
      <div style={{ position: 'absolute', left: '35%', top: '55%', marginTop: '200px', padding: '100px 70px', border: '2px solid #ccc', borderRadius: '5px' }}>
        <form onSubmit={handleSubmit(onSubmit)}>
          <Input name="id" placeholder="Enter ID" ref={register({ required: true })} style={{ width: '150px' }} />
          {errors.id && <span>id is required</span>}
          <br />
          <br />
          <Input.Password
            name="password"
            ref={register({ required: true })}
            placeholder="Enter Password"
            iconRender={(visible) => (visible ? <EyeTwoTone /> : <EyeInvisibleOutlined />)}
            style={{ width: '150px' }}
          />
          {errors.password && <span>password is required</span>}
          <Button type="primary" htmlType="submit" ghost>Primary</Button>
        </form>
      </div>
    </>
  );
};

export default LoginForm;
