import React from 'react';
import { useForm } from 'react-hook-form';
import { useDispatch } from 'react-redux';
import { Input, Button } from 'antd';
import { EyeInvisibleOutlined, EyeTwoTone } from '@ant-design/icons';
import { loginRequestAction } from '../reducers/user';

const LoginForm = () => {
  const { register, handleSubmit, errors } = useForm();

  const dispatch = useDispatch();

  const onSubmit = (data) => {
    console.log(data.id, data.password);
    dispatch(loginRequestAction({
      id: data.id,
      password: data.password,
    }));
  };

  return (
    <>
      <div style={{ padding: '70px 70px', border: '2px solid #ccc', borderRadius: '5px' }}>
        <form onSubmit={handleSubmit(onSubmit)}>
          <label htmlFor="id" style={{ color: 'white', position: 'absolute', left: '2%', top: '80%' }}>ID</label>
          <Input name="id" placeholder="Enter ID" ref={register({ required: true })} style={{ width: '170px' }} />
          {errors.id && <span>id is required</span>}
          <br />
          <br />
          <label htmlFor="password" style={{ color: 'white', position: 'absolute', left: '2%', top: '48%' }}>Password</label>
          <Input.Password
            name="password"
            ref={register({ required: true })}
            placeholder="Enter Password"
            iconRender={(visible) => (visible ? <EyeTwoTone /> : <EyeInvisibleOutlined />)}
            style={{ width: '170px' }}
          />
          {errors.password && <span>password is required</span>}
          <br />
          <Button type="primary" htmlType="submit" ghost style={{ color: 'white', borderColor: 'white', marginTop: '10px', marginRight: '10px' }}>로그인</Button>
          <Button type="primary" htmlType="button" ghost style={{ color: 'white', borderColor: 'white', marginTop: '10px' }}>회원가입</Button>
        </form>
      </div>
    </>
  );
};

export default LoginForm;
