import React, { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { Row, Col, PageHeader } from 'antd';
import AppLayout from '../components/Applayout';
import PostForm from '../components/postForm';
import PostCard from '../components/postCard';
import { LOAD_POSTS_REQUEST } from '../reducers/post';

const FreeBoard = () => {
  const { postInfos, hasMorePosts } = useSelector((state) => state.post);
  const dispatch = useDispatch();

  useEffect(() => {
    dispatch({
      type: LOAD_POSTS_REQUEST,
    });
  }, []);

  useEffect(() => {
    function onScroll() {
      if (window.scrollY + document.documentElement.clientHeight > document.documentElement.scrollHeight - 300) {
        if (hasMorePosts) {
          const lastId = postInfos[postInfos.length - 1]?.id;
          dispatch({
            type: LOAD_POSTS_REQUEST,
            lastId,
          });
        }
      }
    }
    window.addEventListener('scroll', onScroll);
    return () => {
      window.removeEventListener('scroll', onScroll);
    };
  }, [postInfos, hasMorePosts]);


  return (
    <>
      <AppLayout>
        <Row>
          <Col span={20}>
            <div style={{ margin: '10px 30px' }}>
              <PageHeader className="site-page-header" title="자유게시판" subTitle="하냥이들의 소통공간" />
              <PostForm />
              {postInfos.map((post) => <PostCard key={post.id} post={post} />)}
            </div>
          </Col>
          <Col span={4} style={{ backgroundColor: '#50bcdf' }}>
            광고였으면 좋겠다
          </Col>
        </Row>
      </AppLayout>
    </>
  );
};

export default FreeBoard;
