import React from 'react';
import { useForm } from 'react-hook-form';
import { useSelector, useDispatch } from 'react-redux';
import { Input } from 'antd';
import { loginRequestAction } from '../reducers/user';

const LoginForm = () => {

  const { register, handleSubmit, watch, errors } = useForm();
  // console.log(watch('example')); // watch input value by passing the name of it 

  const dispatch = useDispatch();

  const onSubmit = (data) => {
    dispatch(loginRequestAction({
      id: data.id,
      password: data.password,
    }));
    console.log(data.id, data.password);
  };


  return (
    <>
      {/* "handleSubmit" will validate your inputs before invoking "onSubmit" */}
      <form onSubmit={handleSubmit(onSubmit)}>
        {/* register your input into the hook by invoking the "register" function */}
        <input name="id" defaultValue="" ref={register({ required: true })} />
        {errors.id && <span>id is required</span>}
        {/* include validation with required or other standard HTML validation rules */}
        <Input.Password
          placeholder="input password"
          iconRender={visible => (visible ? <EyeTwoTone /> : <EyeInvisibleOutlined />)}
        />
        <input name="password" defaultValue="" ref={register({ required: true })} />
        {/* errors will return when field validation fails  */}
        {errors.password && <span>This is required</span>}
        <input type="submit" />
      </form>
    </>
  );
};

export default LoginForm;
