import React from 'react';
import { useSelector } from 'react-redux';
import { Row, Col, Divider, List, Card } from 'antd';
import AppLayout from '../components/Applayout';

const Profile = () => {
  const { info } = useSelector((state) => state.user);

  const data = [['아이디', info.id], ['이메일', info.email], ['닉네임', info.nickname], ['학과', info.department]];

  return (
    <AppLayout>
      <div className="site-card-wrapper">
        <Row style={{ backgroundColor: '#001529', height: '120px' }}>
          배너
        </Row>
        <Row gutter={16}>
          <Col span={12} style={{ marginTop: '20px', marginLeft: '20px' }}>
            <Divider orientation="left">기본 정보</Divider>
            <List
              bordered
              dataSource={data}
              renderItem={(item) => (
                <List.Item>
                  <strong>{item[0]}</strong>
                  <p>{item[1]}</p>
                </List.Item>
              )}
            />
          </Col>
          <Col span={12}>
            <Card title="활동 정보" bordered={false}>
              Card content
            </Card>
          </Col>
        </Row>
      </div>
    </AppLayout>
  );
};

export default Profile;
