import React, { useEffect } from 'react';
import { useDispatch } from 'react-redux';
import { Card, Avatar } from 'antd';
import { SettingOutlined, EditOutlined, EllipsisOutlined } from '@ant-design/icons';
import propTypes from 'prop-types';
import { LOAD_POSTS_REQUEST } from '../reducers/post';

const { Meta } = Card;

const PostCard = ({ post }) => {
  return (
    <>
      <Card
        style={{ width: 500, margin: '10px auto' }}
        hoverable
        cover={(
          <div>
            <div style={{ display: 'flex', flexDirection: 'row' }}>
              <Avatar src="https://zos.alipayobjects.com/rmsportal/ODTLcjxAfvqbxHnVXCYX.png" style={{ flex: 1 }} />
              <div style={{ flex: 8, display: 'flex', flexDirection: 'column', marginLeft: '7px' }}>
                <strong style={{ flex: 1 }}>게시자 이름</strong>
                <p style={{ flex: 1 }}>7월2일 오후 10시:16</p>
              </div>
            </div>
            <p>안녕하세요!안녕하세요!안녕하세요!안녕하세요!안녕하세요!안녕하세요!안녕하세요!안녕하세요!안녕하세요!안녕하세요!안녕하세요!</p>
            <img alt="example" src="https://gw.alipayobjects.com/zos/rmsportal/JiqGstEfoWAOHiTxclqi.png" />
          </div>
        )}
        actions={[
          <SettingOutlined key="setting" />,
          <EditOutlined key="edit" />,
          <EllipsisOutlined key="ellipsis" />,
        ]}
      >
        <Meta
          title="Card title"
          description="This is the description"
        />
      </Card>
    </>
  );
};

PostCard.propTypes = {
  post: propTypes.array.isRequired,
};

export default PostCard;
